mapper.select:
	select tr_title,tr_content,tr_time,user_nickname,user_idx,tr_idx from tr natural join User

	order by tr_idx desc,tr_time desc
 	limit #{pageStart}, #{perPageNum}

select tr_parent_idx,tr_comment_idx,user_idx,user_nickname,tr_comment_time,tr_comment_content,tr_comment_useable
from trComment natural join User where tr_idx=#{tr_idx}
order by tr_parent_idx, tr_comment_idx
